@article{Regivan:SBMAC:11, author    = {Regivan Santiago and Christian Maeder}, publisher = {SBMAC, Brasil}, title     = {{Linguistic Variables of Type-N - a Mathematical Model}}, journal   = {Tendencias em Matematica Aplicada e Computacional (TEMA)}, number    = {1}, month     = {April}, volume    = {12}, pages     = {21--30}, year      = {2011}, keywords  = {Fuzzy Sets, Type-n Fuzzy sets, systems of type-n, n-homogeneous linguistic variables, heterogeneous linguistic variables}, abstract  = {This work proposes a generalization for the notions of Linguistic Variables and Fuzzification Process. The idea is to provide a concept of fuzzy systems in which the linguistic terms of a linguistic variable are not necessarily connected with fuzzy sets of the same type. Those systems are called here Systems of Type-n. We introduce concepts like n-Homogeneous linguistic variables which emphasizes the occurrence of different levels of uncertainty in such Fuzzy systems. The most important result is the statement of the General Fuzzification Expression (GFE) which establishes how the fuzzification process must be calculated in every system of type-n.}, }

